package corejava;

public class Swithchh {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=3,y=4;
		switch(x+3){
		
		case 6: y=0;
		System.out.println(y);
		case 7: y=1;
		System.out.println(y);
		default: y += 1;
		}
	System.out.println(y);	
	}

}
